module.exports = {
  doPageBase: function($, context) {

      var utils = require("./utils.js") ;
      
      var ret = utils.getScriptByTag( $, "google_conversion_id") ;
      ret=ret+ utils.getScriptByTag( $, "dataLayer") ;
      ret=ret+ utils.getScriptByTag( $, "vizury") ;
      ret=ret+  utils.getScriptByTag( $, "_gaq.push") ;

      if ( ret != undefined  && ret != "")
          context["scriptGoogle"]=ret ;

      var ret2 = utils.getScriptByTag( $, "us-sonar.sociomantic.com") ;
      if ( ret2 != undefined  && ret != "")
          ret2 = ret2.replace("adpan/netfarma-br", "adpan/netfarma-br-m") 
      ret2=ret2+ utils.getScriptByTag( $, "criteo") ;
      ret2=ret2+ utils.getScriptByTag( $, "static.criteo.net");

      if ( ret2 != undefined  && ret != "") {
          context["outrosScripts"]= ret2 ;
      }

      context["cartItems"] = $("#arraste_TotalItensCarrinho").text().replace("(", "").replace(")", "").trim();
      if ( $(".user-nav > p > a").length > 0 ) {        
          context["nomeUsuario"]= $(".user-nav > p > a").first().text().replace(/Login/g, "Entrar").trim();
      }


  }

};
