module.exports = {
  getScriptByTag: function($, tag) {

  	var scripts = $("script").map(function() {
  			var text = $(this).text() ;
  			if ( text.indexOf(tag) != -1 )
                            return $(this);
                  }).get();	

  	var ret="";
  	for(var x=0; x< scripts.length ; x++ ) {
  		ret=ret+scripts[x];
  	}

    return ret;

  }

};